import React, { useState, useRef } from "react";
import "./Navbar.scss";

function Navbar() {
  const [isActive, setActive] = useState(false);
  const sectionRefs = {
    experience: useRef(null),
    projects: useRef(null),
    contact: useRef(null),
  };

  const toggleNavbar = () => {
    setActive(!isActive);
  };

  const scrollToSection = (section) => {
    sectionRefs[section].current.scrollIntoView({
      behavior: "smooth",
    });
    // Close the mobile navigation menu if it's open
    if (isActive) {
      setActive(false);
    }
  };

  return (
    <nav className="navbar">
      <div className="brand-title">Your Brand</div>
      <div className="toggle-button" onClick={toggleNavbar}>
        <span className="bar"></span>
        <span className="bar"></span>
        <span className="bar"></span>
      </div>
      <div className={isActive ? "active navbar-links" : "navbar-links"}>
        <ul>
          <li>
            <a onClick={() => scrollToSection("experience")}>
              <span>01.</span> Experience
            </a>
          </li>
          <li>
            <a onClick={() => scrollToSection("projects")}>
              <span>02.</span> Projects
            </a>
          </li>
          <li>
            <a onClick={() => scrollToSection("contact")}>
              <span>03.</span> Contact
            </a>
          </li>
        </ul>
      </div>
    </nav>
  );
}

export default Navbar;
